#include "define.h"
#include "acllib.h"
player::player()
{
	speed = player_speed;
	scores = 0;
	blood = blood_volume;
	loadImage("player.jpg", icon);
	x = (getWidth() - big_icon_size) / 2;
	y = (getHeight() - big_icon_size) / 2;
}
void player::control(int mode)
{
	switch (mode)
	{
	case 1:
		if (y - speed > 0)
			y -= speed;
		break; //向上
	case 2:
		if (y + speed < getHeight() - big_icon_size)
			y += speed;
		break; //向下
	case 3:
		if (x - speed > 0)
			x -= speed;
		break; //向左
	case 4:
		if (x + speed < getWidth() - big_icon_size)
			x += speed;
		break; //向右
	default:
		break;
	}
}
int player::get_blood()
{
	return blood;
}
void player::blood_down(int damage)
{
	blood-=damage;
}
int player::get_scores()
{
	return scores;
}                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                

void player::addscores(int score)
{
	scores +=score;
}
void player::addspeed(int s)
{
	speed += s;
}
void player::draw()
{
	putImageTransparent(icon, x, y, big_icon_size, big_icon_size, WHITE);
}
